import React from "react";

const Dashboard = () => {
  // Placeholder data for 10 detections
  const placeholderDetections = Array.from({ length: 10 }, (_, index) => ({
    id: index + 1,
    label: "Bear",
    description: "A bear was detected near the northeast section of the monitoring area. The system flagged unusual movement consistent with past bear detections.",
    timestamp: `April ${10 - index}, 2025 - ${9 + index}:00 AM`,
    image: "https://via.placeholder.com/500x300.png?text=Detection"
  }));

  return (
    <div style={{ backgroundColor: "#323232", minHeight: "100vh", padding: "2rem", color: "white" }}>
      <h2 className="text-center mb-5" style={{ color: "#ffd400" }}>Detections</h2>
      <div className="container">
        {placeholderDetections.map((detection) => (
          <div
            key={detection.id}
            className="mb-5 p-4 rounded"
            style={{ backgroundColor: "#3a3a3a", boxShadow: "0 0 15px rgba(0,0,0,0.4)" }}
          >
            <img
              src={detection.image}
              alt={`Detection ${detection.id}`}
              className="img-fluid rounded mb-3"
              style={{ width: "100%", maxHeight: "350px", objectFit: "cover" }}
            />
            <h4 style={{ color: "#ffd400" }}>{detection.label}</h4>
            <p style={{ fontSize: "1rem", lineHeight: "1.6", color: "#e0e0e0" }}>
              {detection.description}
            </p>
            <p className="text-white-50" style={{ fontSize: "0.9rem" }}>{detection.timestamp}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
